import React, { Component } from "react";
import StudentDataService from "./services/student.service";
import './App.css';

class App extends Component {
  constructor (props) {
    super(props);
    this.state = {students: []};
    const [formValue, setformValue] = React.useState({
      name: '',
      gender: '',
      DOB: '',
      specialized: ''
    });
  }

  componentDidMount(){
    this.fetchAllStudents();
  }

  fetchAllStudents() {
    StudentDataService.getAll()
      .then(response => {
        this.setState({
          students: response.data
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  handleSubmit = async() => {
    // store the states in the form data
    const loginFormData = new FormData();
    loginFormData.append("name", formValue.name)
    loginFormData.append("gender", formValue.gender)
    loginFormData.append("DOB", formValue.DOB)
    loginFormData.append("specialized", formValue.specialized)

    try {
      // make axios post request
      const response = await axios({
        method: "post",
        url: "/api/login",
        data: loginFormData,
        headers: { "Content-Type": "multipart/form-data" },
      });
    } catch(error) {
      console.log(error)
    }
  }

  handleChange = (event) => {
    setformValue({
      ...formValue,
      [event.target.name]: event.target.value
    });
  }

    render() {
      return (
        <div className="App">
          <header className="App-header">
            <table class='table'>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Gender</th>
                <th>Date of Birth</th>
                <th>Specialization</th>
              </tr>
            {this.state.students.map(student => 
              <tr>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.gender}</td>
                <td>{Date(student.DOB)}</td>
                <td>{student.specialized}</td>
              </tr>
              )}
            </table>
            <form onSubmit={handleSubmit}>
              <p>Add a new student form</p>
              <input
                name="name"
                placeholder="enter an name"
                value={formValue.name}
                onChange={handleChange}
              />
              <input
                type="gender"
                name="gender"
                placeholder="enter a gender"
                value={formValue.gender}
                onChange={handleChange}
              />
              <input
                type="date"
                name="dob"
                placeholder="enter a date of birth"
                value={formValue.dob}
                onChange={handleChange}
              />
              <input
                name="specialized"
                placeholder="enter an specialization"
                value={formValue.specialized}
                onChange={handleChange}
              />
              <button type="submit">
                Login
              </button>
          </form>
          </header>
        </div>
      );
    }

}

export default App;
