import React, { Component, useState } from "react";
import StudentDataService from "./services/student.service";
import './App.css';

class App extends Component {
  constructor (props) {
    super(props);
    this.state = {
        students: [],
        formValue: {
          name: '',
          gender: '',
          DOB: '',
          specialized: ''
        }

    };
  }

  componentDidMount(){
    this.fetchAllStudents();
  }

  fetchAllStudents() {
    StudentDataService.getAll()
      .then(response => {
        this.setState({
          students: response.data
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  handleSubmit = async() => {
    const loginFormData = new FormData();
    loginFormData.append("name", this.state.formValue.name)
    loginFormData.append("gender", this.state.formValue.gender)
    loginFormData.append("DOB", this.state.formValue.DOB)
    loginFormData.append("specialized", this.state.formValue.specialized)

    try {
      StudentDataService.create(loginFormData)
      .then((err) => console.log(err))
    } catch(error) {
      console.log(error)
    }
  }

  handleChange = (event) => {
    setformValue({
      ...this.state.formValue,
      [event.target.name]: event.target.value
    });
  }

    render() {
      return (
        <div className="App">
          <header className="App-header">
            <table class='table'>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Gender</th>
                <th>Date of Birth</th>
                <th>Specialization</th>
              </tr>
            {this.state.students.map(student => 
              <tr>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.gender}</td>
                <td>{Date(student.DOB)}</td>
                <td>{student.specialized}</td>
              </tr>
              )}
            </table>
            <form onSubmit={handleSubmit}>
              <p>Add a new student form</p>
                <input
                  name="name"
                  placeholder="enter an name"
                  value={this.state.formValue.name}
                  onChange={this.handleChange}
                />
                <input
                  type="gender"
                  name="gender"
                  placeholder="enter a gender"
                  value={this.state.formValue.gender}
                  onChange={this.handleChange}
                />
                <input
                  type="date"
                  name="dob"
                  placeholder="enter a date of birth"
                  value={this.state.formValue.dob}
                  onChange={this.handleChange}
                />
                <input
                  name="specialized"
                  placeholder="enter an specialization"
                  value={this.state.formValue.specialized}
                  onChange={this.handleChange}
                />
              <button type="submit">
                Login
              </button>
          </form>
          </header>
        </div>
      );
    }

}

export default App;
