import React, { Component } from "react";
import logo from './logo.svg';
import StudentDataService from "./services/student.service";
import './App.css';

class App extends Component {
  constructor (props) {
    super(props);
    this.state = {students: []};
  }

  componentDidMount(){
    this.fetchAllStudents();
  }

  fetchAllStudents() {
    StudentDataService.getAll()
      .then(response => {
        this.setState({
          students: response.data
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

    render() {
      return (
        <div className="App">
          <header className="App-header">
            <table class='table'>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Gender</th>
                <th>Date of Birth</th>
                <th>Specialization</th>
              </tr>
            {this.state.students.map(student => 
              <tr>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.gender}</td>
                <td>{student.DOB.toDateString('en-us')}</td>
                <td>{student.specialized}</td>
              </tr>
              )}
            </table>

          </header>
        </div>
      );
    }

}

export default App;
